function slider(){$(".slider").slick({autoplay:!0,autoplaySpeed:2e4,responsive:[{breakpoint:1024,settings:{slidesToShow:1,slidesToScroll:1,infinite:!0}},{breakpoint:600,settings:{slidesToShow:1,slidesToScroll:1,infinite:!0}},{breakpoint:480,settings:{slidesToShow:1,slidesToScroll:1,infinite:!0}}]})}function burgermenu(){$(".navbar-menu li:last-child").click(function(){$(".icon").toggleClass("rotate"),$(".navbar-menu").toggleClass("active-menu"),$("body").toggleClass("overflow-hide")})}slider(),burgermenu();